

use IO::All -qw(utf8);

my $all = io( 'dist/css/bootstrap.css')->slurp();

print length($all) . "\n";

$all =~ s#//.*##g;
#$all =~ s#/*.*?*/*##sg;

$all =~ s/\{[^\{]*?\}//sg;

@all = split /\./, $all;

@all = sort @all;

foreach my $all ( @all )
	{
	$all =~ s/,//;
	$all =~ s/:.*$//s;
	$all =~ s/\s.*$//s;
	$keys{$all}++;
	}

foreach my $key ( sort keys %keys )
	{
	print "$key\n";
	}

